<?php

// Replace these values with your actual database credentials
$servername = "localhost";
$username = "root";
$password = "mysql123";
$dbname = "attendance_management_system";

try {
    // Create connection
    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);
    
    // Set the PDO error mode to exception
    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);

    // Update records with status 1 and set exit timestamp to current time
    $updateStmt = $conn->prepare("UPDATE Library_ledger SET status = 0, `exit` = CURRENT_TIMESTAMP WHERE status = 1");
    $updateStmt->execute();

    $updateStmt = $conn->prepare("UPDATE Reference_section SET status = 0, `exit` = CURRENT_TIMESTAMP WHERE status = 1");
    $updateStmt->execute();

    echo "Records updated successfully";

} catch (PDOException $e) {
    echo "Error: " . $e->getMessage();
}

// Close the connection
$conn = null;

?>