<?php
    $con = mysqli_connect("lib-jss.ct4teqpkgci2.us-east-1.rds.amazonaws.com","admin","mysql123","attendance_management_system");
    //$con = mysqli_connect('127.0.0.1','root','mysql123','attendance_management_system');
    if(!$con)
    {
        echo 'Not connected to the server';
    }

    if(!mysqli_select_db($con, 'attendance_management_system'))
    {
        echo 'Database not selected';
    }

//include database configuration file
// include 'dbConfig.php';

//get records from database

$query = $con->query("SELECT lib_id,name,phone,Em_Contact,Blood,address,USN,Date_of_birth,branch,type,photo FROM `Students`");

if($query->num_rows > 0){
    $delimiter = ",";
    $filename = "library_report_" . date('Y-m-d') . ".csv";
    
    //create a file pointer
    $f = fopen('php://memory', 'w');
    
    //set column headers
    //$fields = array('Name','Branch','Semester','Lib ID', 'Status','Entry Time', 'Exit Time', 'Elapsed Time', 'Feedback');
    $fields = array('SR Number','Name','Phone Number','Emergency Contact','Blood Group','Address','USN','Date of birth','Branch', 'Programme','Photo URL');
    fputcsv($f, $fields, $delimiter);
    
    //output each row of the data, format line as csv and write to file pointer
    while($row = $query->fetch_assoc()){
        //$lineData = array($row['name'], $row['branch'], $row['semester'], $row['lib_id'], $row['status'], $row['entry'], $row['exit'], $row['elapsed_time'], $row['SUGGESTION']);
        $lineData = array($row['lib_id'],$row['name'], $row['phone'], $row['Em_Contact'], $row['Blood'], $row['address'], $row['USN'], $row['Date_of_birth'], $row['branch'],$row['type'],$row['photo']);
        fputcsv($f, $lineData, $delimiter);
    }
    
    //move back to beginning of file
    fseek($f, 0);
    
    //set headers to download file rather than displayed
    header('Content-Type: text/csv');
    header('Content-Disposition: attachment; filename="' . $filename . '";');
    
    //output all remaining data on a file pointer
    fpassthru($f);
}
exit;

